from machine import Pin, PWMimport timeclass Telepass:    def __init__(self, pin_id=4):        self.sensore = Pin(pin_id, Pin.IN)        self.buzzer_pin_id = 18        self._occupato_precedente = False        self.buzzer_pin = Pin(self.buzzer_pin_id, Pin.OUT)        self.buzzer_pin.off()        try:            tmp_pwm = PWM(self.buzzer_pin)            tmp_pwm.deinit()        except:            pass    def _bip(self, durata):        pwm = PWM(Pin(self.buzzer_pin_id))        pwm.freq(4500)        pwm.duty(512)        time.sleep(durata)        pwm.duty(0)        pwm.deinit()        clean_pin = Pin(self.buzzer_pin_id, Pin.OUT)        clean_pin.off()        time.sleep_ms(10)    def esegui_suono(self, modalita):        if modalita == 0:            self._bip(0.1)            time.sleep(0.05)            self._bip(0.1)        else:            self._bip(0.05)            time.sleep(0.03)            self._bip(0.2)    def veicolo_rilevato(self, modalita_attuale):        stato_attuale = (self.sensore.value() == 0)        if stato_attuale and not self._occupato_precedente:            time.sleep_ms(50)            if self.sensore.value() == 0:                self._occupato_precedente = True                print(f"ðŸš— Rilevato! ModalitÃ  {modalita_attuale}")                self.esegui_suono(modalita_attuale)                return True        if not stato_attuale and self._occupato_precedente:            print("ðŸ”„ Corsia libera")            self._occupato_precedente = False        return False