from Dispositivo import Dispositivoclass Totem(Dispositivo):    def __init__(self, status, corsia, casello, dispositivo_id,schermo, ir):        super().__init__(            status=status,            corsia=corsia,            casello=casello,            tipo_dispositivo="TOTEM",            dispositivo_id=dispositivo_id        )        self.schermo = schermo        self.ir = ir