class Veicolo():    def __init__(self, targa, classe_veicolo):        self.targa = targa        self.classe_veicolo = classe_veicolo.upper()        # Definiamo la lista delle tariffe per ogni classe        # A = 0 / B = 1 / C = 2 / D = 3 / E = 4 / F = 5        tariffe = [0.05, 0.07, 0.09, 0.12, 0.15]        # La funzione ord restituisce un intero corrispondente all'ASCII del carattere preso in input        # A = 65 / B = 66 / C = 67 / D = 68 / E = 69 / F = 70        indice = ord(self.classe_veicolo) - ord('A')        if 0 <= indice < len(tariffe):            self.prezzo_km = tariffe[indice]        else:            self.prezzo_km = 0.0  # Valore di errore